function tarjetasDePersonajes() {
    let marvelContenedor = document.getElementById('personajes-marvel');
    let dcContenedor = document.getElementById('personajes-dc');
    
    
    marvelContenedor.innerHTML = '';
    dcContenedor.innerHTML = '';

    personajesDC.forEach(function(personaje) {
        let tarjeta = crearTarjeta(personaje);
        dcContenedor.appendChild(tarjeta);
    });

    personajesMarvel.forEach(function(personaje) {
        let tarjeta = crearTarjeta(personaje);
        marvelContenedor.appendChild(tarjeta);
    });
}

function crearTarjeta(personaje) {
    let tarjeta = document.createElement('div');
    tarjeta.classList.add('tarjeta');

    let image = document.createElement('img');
    image.src = personaje.picture;
    image.alt = personaje.name;

    let name = document.createElement('h3');
    name.textContent = personaje.name;

    let button = document.createElement('button');
    button.textContent = 'Ver';
    button.classList.add('button');

    button.addEventListener('click', function() {
        mostrarDetalle(personaje);
    });

    tarjeta.appendChild(image);
    tarjeta.appendChild(name);
    tarjeta.appendChild(button);

    return tarjeta;
}

// 

tarjetasDePersonajes();

function mostrarDetalle(personaje) {
    let ventana = document.getElementById('ventanaDesc');


    ventana.innerHTML = `
    <div class='contenidoVentana'>
        <img src='${personaje.picture}' alt='${personaje.name}'>
        <h2>${personaje.name}</h2>
        <p>${personaje.about}</p>
        <button id='cerrar' class='btnVentana'>X</button>
    </div>
`;

    document.getElementById('cerrar').addEventListener('click', function() {
        ventana.style.display = 'none';
    });

    ventana.style.display = 'block';
}

//Buscar

document.getElementById('search-bar').addEventListener('input', function() {
    filtrarPersonajes(this.value);
});

function filtrarPersonajes(input) {
    input = input.toLowerCase();
    let personajes = document.querySelectorAll('.tarjeta');
    personajes.forEach(function(personaje) {
        let nombre = personaje.querySelector('h3').textContent.toLowerCase();
        if (nombre.includes(input)) {
            personaje.style.display = 'block';
        } else {
            personaje.style.display = 'none';
        }
    });
}

//dropmenu personajes

function filtrarPorUniverso(opcion) {
    let marvelContenedor = document.getElementById('personajes-marvel');
    let dcContenedor = document.getElementById('personajes-dc');
    
    if (opcion === 'Marvel') {
        mostrarContenedor(marvelContenedor);
        ocultarContenedor(dcContenedor);
    } else if (opcion === 'DC') {
        ocultarContenedor(marvelContenedor);
        mostrarContenedor(dcContenedor);
    } else {
        mostrarContenedor(marvelContenedor);
        mostrarContenedor(dcContenedor);
    }
}

function ocultarContenedor(contenedor) {
    let tarjetas = contenedor.querySelectorAll('.tarjeta');
    tarjetas.forEach(function(tarjeta) {
        tarjeta.style.display = 'none';
    });
}

function mostrarContenedor(contenedor) {
    let tarjetas = contenedor.querySelectorAll('.tarjeta');
    tarjetas.forEach(function(tarjeta) {
        tarjeta.style.display = 'block';
    });
}


